import React, { useState } from 'react';

const ROWS = 6;
const COLS = 7;

export default function Connect4 () {
  const [board, setBoard] = useState(Array(ROWS).fill(Array(COLS).fill(null)));
  const [currentPlayer, setCurrentPlayer] = useState('Red');
  const [winner, setWinner] = useState(null);

  const checkWin = (board) => {
    const checkDirection = (row, col, rowStep, colStep) => {
      let count = 0;
      let color = board[row][col];
      for (let i = 0; i < 4; i++) {
        const r = row + i * rowStep;
        const c = col + i * colStep;
        if (r >= 0 && r < ROWS && c >= 0 && c < COLS && board[r][c] === color) {
          count++;
        } else {
          break;
        }
      }
      return count === 4;
    };

    for (let row = 0; row < ROWS; row++) {
      for (let col = 0; col < COLS; col++) {
        if (
          board[row][col] &&
          (checkDirection(row, col, 0, 1) || // Horizontal
           checkDirection(row, col, 1, 0) || // Vertical
           checkDirection(row, col, 1, 1) || // Diagonal down-right
           checkDirection(row, col, 1, -1))  // Diagonal down-left
        ) {
          return board[row][col];
        }
      }
    }
    return null;
  };

  const handleClick = (col) => {
    if (winner) return;

    const newBoard = board.map(row => row.slice());
    for (let row = ROWS - 1; row >= 0; row--) {
      if (!newBoard[row][col]) {
        newBoard[row][col] = currentPlayer;
        setBoard(newBoard);
        const checkWinner = checkWin(newBoard);
        if (checkWinner) {
          setWinner(checkWinner);
        } else {
          setCurrentPlayer(currentPlayer === 'Red' ? 'Blue' : 'Gray');
        }
        return;
      }
    }
  };

  const renderCell = (row, col) => (
    <div
      key={`${row}-${col}`}
      onClick={() => handleClick(col)}
      style={{
        ...styles.cell,
        backgroundColor: board[row][col] || 'white',
        cursor: winner ? 'default' : 'pointer',
      }}
    />
  );

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Connect 4 Game</h1>
      <p style={styles.description}>Play against the AI! Make your move by dragging the pieces.

      </p>
      {winner && (
        <div style={styles.winner}>
          {winner} wins!
        </div>
      )}
      <div style={styles.gameContainer}>
        <div style={styles.boardWrapper}>
          {Array.from({ length: ROWS }).map((_, row) =>
            Array.from({ length: COLS }).map((_, col) => renderCell(row, col))
          )}
        </div>
        <div style={styles.moveHistory}>
          <p style={styles.history}>Move History</p>
          <ul style={styles.historyList}>
            <li>Move tracking not yet implemented</li>
          </ul>
        </div>
      </div>
      <button
        onClick={() => {
          setBoard(Array(ROWS).fill(Array(COLS).fill(null)));
          setCurrentPlayer('Red');
          setWinner(null);
        }}
        style={styles.restartButton}
      >
        Restart Game
      </button>
    </div>
  );
}

const styles = {
  container: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    marginTop: '50px',
  },
  title: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  description: {
    fontSize: '16px',
    color: '#666',
    marginBottom: '20px',
    textAlign: 'center',
    maxWidth: '400px',
  },
  gameContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    alignItems: 'center',
  },
  boardWrapper: {
    display: 'grid',
    gridTemplateColumns: `repeat(${COLS}, 50px)`,
    gap: '5px',
    border: '2px solid #333',
    padding: '10px',
    borderRadius: '8px',
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.3)',
    backgroundColor: '#000000',
  },
  cell: {
    width: '50px',
    height: '50px',
    borderRadius: '50%',
    border: '1px solid black',
  },
  winner: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#d9534f',
    marginTop: '20px',
  },
  moveHistory: {
    width: '150px',
    height: '300px',
    overflowY: 'scroll',
    backgroundColor: '#e0e0e0',
    borderRadius: '8px',
    padding: '10px',
    boxShadow: '0px 4px 8px rgba(0, 0, 0, 0.2)',
  },
  history: {
    fontSize: '16px',
    fontWeight: 'bold',
    marginBottom: '10px',
  },
  historyList: {
    listStyleType: 'none',
    paddingLeft: 0,
    fontSize: '14px',
    color: '#333',
  },
  restartButton: {
    marginTop: '20px',
    padding: '10px 20px',
    fontSize: '16px',
    fontWeight: 'bold',
    color: '#fff',
    backgroundColor: '#007bff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  },
};
