import { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

export default function Board() {
  const [game, setGame] = useState(new Chess());
  const [pos, ] = useState([game.fen()]);

  const makeMove = (move) => {

    game.move(move)
    pos.push(game.fen())
    setGame(new Chess(game.fen()))
  }

  function makeRandomMove(possibleMoves) {

    if (game.isGameOver() || game.isDraw() || possibleMoves.length === 0) return; // exit if the game is over
    const randomIndex = Math.floor(Math.random() * possibleMoves.length);
    makeMove(possibleMoves[randomIndex]);
  }

   function selectMove(){
    const possibleMoves = game.moves();
    console.log(possibleMoves)

    const posBoards = [];
    for (const move of possibleMoves){
      game.move(move);
      posBoards.push(game.fen());
      game.undo()

    }
    console.log(posBoards)
    makeRandomMove(possibleMoves)
   }



  function onDrop(sourceSquare, targetSquare) {
    try{
      makeMove({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // always promote to a queen for example simplicity
      });

      // illegal move
      setTimeout(selectMove, 200);
      return true;
    }
    catch (e) { console.log(e)}
  }


  return(
     <div>
        <Chessboard position={game.fen()} onPieceDrop={onDrop} autoPromoteToQueen={true} boardWidth={400}/>
     </div>
  )
}
