import { useState } from "react";
import { Chess } from "chess.js";
import { Chessboard } from "react-chessboard";

export default function Board() {
  const [game, setGame] = useState(new Chess());
  const [states, updateState] = useState([game.fen()]);

  const makeMove = (move) => {
    game.move(move)
    updateState(states.append(game.fen()))
    setGame(new Chess(game.fen()))
  }

  function makeRandomMove() {
    const possibleMoves = game.moves();
    if (game.isGameOver() || game.isDraw() || possibleMoves.length === 0) return; // exit if the game is over
    const randomIndex = Math.floor(Math.random() * possibleMoves.length);
    makeMove(possibleMoves[randomIndex]);
  }

  function onDrop(sourceSquare, targetSquare) {
    try{
      makeMove({
        from: sourceSquare,
        to: targetSquare,
        promotion: "q", // always promote to a queen for example simplicity
      });

      // illegal move
      setTimeout(makeRandomMove, 200);
      return true;
    }
    catch{}
  }

  return(
     <div>
        <Chessboard position={game.fen()} onPieceDrop={onDrop} boardWidth={400}/>
     </div>
  )
}
