import React, { useState } from 'react';
import { Chessboard } from 'react-chessboard';

const Chess = () => {
    const [gamePosition, setGamePosition] = useState('start');

    const onDrop = ({ sourceSquare, targetSquare }) => {
      // Update the game position after a piece is dropped
      // This is a placeholder for actual game logic
      setGamePosition(prev => ({ ...prev, [targetSquare]: prev[sourceSquare], [sourceSquare]: null }));
    };

    return (
        <>
        <Chessboard
            id="InteractiveChessboard"
            position={gamePosition}
            onPieceDrop={onDrop}
        />
      </>
    );
  }

export default Chess;
